import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

const BASE_URL = "http://localhost:9292";

const AppTable = styled.table`
margin: auto;


border-collapse: collapse;

td, th {
  border: 1px solid #dddddd;
  text-align: left;
  padding: 8px;
}

tr:nth-child(even) {
  background-color: #dddddd;
}
`;

function AppointmentDetails() {
  const { id } = useParams();
  const [appointment, setAppointment] = useState(null);

  useEffect(() => {
    fetch(BASE_URL + `/appointments/${id}`)
      .then((r) => r.json())
      .then((appointment) => setAppointment(appointment));
  }, []);

  if (!appointment) return <h2>Loading appointment data...</h2>;

  return (
    <div>
      <AppTable>
        <tr>
          <td>Date</td>
          <td>{appointment.appointment_date}</td>
        </tr>
        <tr>
          <td>Duration</td>
          <td>{appointment.appointment_duration} minutes</td>
        </tr>
        <tr>
          <td>Type</td>
          <td>{appointment.appointment_type}</td>
        </tr>
        <tr>
          <td>Reason</td>
          <td>{appointment.appointment_reason}</td>
        </tr>
        <tr>
          <td>Doctor's name</td>
          <td>
            {appointment.doctor.doctor_lastname},{" "}
            {appointment.doctor.doctor_firstname}
          </td>
        </tr>

        <tr>
          <td>Doctor's email</td>
          <td>{appointment.doctor.doctor_email}</td>
        </tr>
        <tr>
          <td>Doctor's phone</td>
          <td>{appointment.doctor.doctor_phone}</td>
        </tr>
        <tr>
          <td>Patient's name</td>
          <td>
            {appointment.patient.patient_lastname},{" "}
            {appointment.patient.patient_firstname}
          </td>
        </tr>
        <tr>
          <td>Patient's phone</td>
          <td>{appointment.patient.patient_phone}</td>
        </tr>
        <tr>
          <td>Patient's email</td>
          <td>{appointment.patient.patient_email}</td>
        </tr>
      </AppTable>

    </div>
  );
}

export default AppointmentDetails;
